mapper.select:
        SELECT
		    count(*)
        FROM
            hqm_pfmea_detail hi
        WHERE
            hi.branch_name =#{branchName}
            and hi.parent_branch_id = #{parentBranchId}
            and hi.fmea_id = #{fmeaId}

    SELECT hi.invalid_id
	 FROM hqm_pfmea_detail hd, hqm_pfmea_detail hd2, hqm_p_invalid hi
	WHERE hd.branch_id = hd2.parent_branch_id
		AND hd.branch_name = hi.invalid_name 
    and hd2.branch_id= #{branchId}   
    and hi.parent_invalid_id = #{parentBranchId}

SELECT * FROM (SELECT a.fmea_name, a.fmea_code, a.branch_name structure_name, b.branch_name function_name ,b.claim ,b.potential_failure_mode ,b.invalid_consequence ,b.severity ,b.special_characteristic_type ,b.failure_reason ,b.preventive_measure ,b.occurrence ,b.detection_measure ,b.detection ,b.rpn ,b.suggest_measure ,su.user_name use_name ,b.estimated_finish_time ,b.measure_result ,b.post_severity ,b.post_occurrence ,b.post_detection ,b.post_rpn ,b.charge_id FROM (SELECT hdd.fmea_id, hf.fmea_name, hf.fmea_code, hdd.ranks, hdd.branch_id, hdd.parent_branch_id, hdd.branch_name FROM hqm_fmea hf LEFT JOIN hqm_pfmea_detail hdd ON hf.kid = hdd.fmea_id WHERE hdd.ranks = 1 AND hf.fmea_type = 'P') a LEFT JOIN (SELECT hdd.fmea_id, hf.fmea_name, hdd.ranks, hdd.branch_id, hdd.parent_branch_id, hdd.branch_name ,hdd.invalid_consequence ,hdd.severity ,hdd.special_characteristic_type ,hdd.failure_reason ,hdd.preventive_measure ,hdd.occurrence ,hdd.detection_measure ,hdd.detection ,hdd.rpn ,hdd.suggest_measure ,hdd.charge_id ,hdd.estimated_finish_time ,hdd.measure_result ,hdd.post_severity ,hdd.post_occurrence ,hdd.post_detection ,hdd.post_rpn ,hdd.claim ,hdd.potential_failure_mode FROM hqm_fmea hf LEFT JOIN hqm_pfmea_detail hdd ON hf.kid = hdd.fmea_id WHERE hdd.ranks = 2) b ON a.branch_id = b.parent_branch_id LEFT JOIN sys_user su on su.user_id = b.charge_id ) temp where 1=1
AND temp.rpn >= #{rpn}
AND temp.post_rpn >= #{postRpn}
AND temp.potential_failure_mode = #{potentialFailureMode}
AND temp.claim = #{claim}
AND temp.fmea_code LIKE concat('%',concat(#{fmeaCode},'%'))
AND temp.fmea_name LIKE concat('%',concat(#{fmeaName},'%'))
AND temp.structure_name LIKE concat('%',concat(#{structureName},'%'))
AND temp.function_Name LIKE concat('%',concat(#{functionName},'%'))
AND temp.branch_name LIKE concat('%',concat(#{branchName},'%'))
AND temp.failure_reason LIKE concat('%',concat(#{failureReason},'%'))
AND temp.invalid_consequence LIKE concat('%',concat(#{failureConsequences},'%'))
AND temp.preventive_measure LIKE concat('%',concat(#{preventiveMeasure},'%'))
AND temp.detection_measure LIKE concat('%',concat(#{detectionMeasure},'%'))
AND temp.charge_id = #{chargeId}
AND temp.suggest_Measure LIKE concat('%',concat(#{suggestMeasure},'%'))
AND temp.measure_result LIKE concat('%',concat(#{measureResult},'%'))
AND temp.estimated_finish_time >= to_date(#{estimatedFinishTimeStart}, 'yyyy-mm-dd hh24:mi:ss')
AND temp.estimated_finish_time <= to_date(#{estimatedFinishTimeEnd}, 'yyyy-mm-dd hh24:mi:ss')
AND temp.post_detection IN
#{postDetection}
AND temp.severity IN
#{severity}
AND temp.occurrence IN
#{occurrence}
AND temp.detection IN
#{detection}
AND temp.post_severity IN
#{postSeverity}
AND temp.post_occurrence IN
#{postOccurrence}
AND temp.special_characteristic_type IN
#{specialCharacteristicType}

mapper.update:
UPDATE HQM_PFMEA_DETAIL SET
BRANCH_NAME = #{branchName},
INVALID_CONSEQUENCE = #{invalidConsequence}, CLAIM = #{claim}, POTENTIAL_FAILURE_MODE = #{potentialFailureMode}, SEVERITY = #{severity}, SPECIAL_CHARACTERISTIC_TYPE = #{specialCharacteristicType}, FAILURE_REASON = #{failureReason}, PREVENTIVE_MEASURE = #{preventiveMeasure}, OCCURRENCE = #{occurrence}, DETECTION_MEASURE = #{detectionMeasure}, DETECTION = #{detection}, RPN = #{rpn}, SUGGEST_MEASURE = #{suggestMeasure}, CHARGE_ID = #{chargeId}, ESTIMATED_FINISH_TIME = #{estimatedFinishTime}, MEASURE_RESULT = #{measureResult}, POST_OCCURRENCE = #{postOccurrence}, POST_SEVERITY = #{postSeverity}, POST_DETECTION = #{postDetection}, POST_RPN = #{postRpn} WHERE BRANCH_ID = #{branchId}
